// Получаем весь текст из входящих данных
const text = items[0].json.plain_text;

// Разбиваем по переносу строки (учитывает \n и \r\n)
const lines = text.split(/\r?\n/);

// Ищем строку, начинающуюся с "Регион покрытия:"
let region = null;
for (const line of lines) {
  const match = line.match(/^Регион покрытия:\s*(.+)$/);
  if (match) {
    region = match[1].trim();
    break;
  }
}

// Отдаём новый объект с извлечённым регионом
return [{
  json: {
    regionCoverage: region
  }
}];
